import "./App.css";
import React from "react";
import axios from "axios";
// import { connect } from "react-redux";
// import { fetchNumbersfromApi } from "./redux/numbers";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      guessInputs: ["x", "x", "x", "x"],
      sideScores: [],
      secretCode: [],
    };
  }
  async componentDidMount() {
    try {
      const res = await axios.get(
        "https://www.random.org/integers/?num=4&min=0&max=7&col=1&base=10&format=plain&rnd=new"
      );

      this.setState({ secretCode: res.data });
    } catch (err) {
      console.log(err);
    }
  }

  handleClick = () => {};

  render() {
    console.log("hey", this.props.secretCode);

    return (
      <div className="App">
        <header>
          <h2>Mastermind Game</h2>
        </header>
        <div className="menu">
          <button className="new-game">New Game</button>
          <button className="reset">Reset</button>
          <button className="guess">Guess</button>
        </div>
        <div className="hidden-circles">
          <span className="dot"></span>
          <span className="dot"></span>
          <span className="dot"></span>
          <span className="dot"></span>
        </div>
        <div className="guessed-circles"></div>
        <div className="coloredButton">
          <button className="" onClick={handleClick}>
            blue
          </button>
          <button className="" onClick={handleClick}>
            red
          </button>
          <button className="" onClick={handleClick}>
            yellow
          </button>
          <button className="" onClick={handleClick}>
            green
          </button>
          <button className="" onClick={handleClick}>
            purple
          </button>
          <button className="" onClick={handleClick}>
            violet
          </button>
          <button className="" onClick={handleClick}>
            orange
          </button>
          <button className="" onClick={handleClick}>
            pink
          </button>
        </div>
      </div>
    );
  }
}

