import "./App.css";
import React from "react";
import axios from "axios";
// import { connect } from "react-redux";
// import { fetchNumbersfromApi } from "./redux/numbers";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      guessInputs: ["x", "x", "x", "x"],
      sideScores: [],
      secretCode: [],
    };
  }
  async componentDidMount() {
    try {
      const res = await axios.get(
        "https://www.random.org/integers/?num=4&min=0&max=7&col=1&base=10&format=plain&rnd=new"
      );

      this.setState({ secretCode: [res.data] });
    } catch (err) {
      console.log(err);
    }
  }

  handleClick = (number) => {
    console.log(number);
    const { quessInputs } = this.state;
    quessInputs.push(number);
    this.setState({ quessInputs });
  };

  render() {
    console.log("hey", this.state.secretCode);

    return (
      <div className="App">
        <header>
          <h2>Mastermind Game</h2>
        </header>
        <div className="menu">
          <button className="new-game">New Game</button>
          <button className="reset">Reset</button>
          <button className="guess">Guess</button>
        </div>
        <div className="hidden-circles">
          <span className="dot"></span>
          <span className="dot"></span>
          <span className="dot"></span>
          <span className="dot"></span>
        </div>
        <div className="guessed-circles"></div>
        <div className="coloredButton">
          <button className="" onClick={() => this.handleClick(1)}>
            blue
          </button>
          <button className="" onClick={() => this.handleClick(2)}>
            red
          </button>
          <button className="" onClick={() => this.handleClick(3)}>
            yellow
          </button>
          <button className="" onClick={() => this.handleClick(4)}>
            green
          </button>
          <button className="" onClick={() => this.handleClick(5)}>
            purple
          </button>
          <button className="" onClick={() => this.handleClick(6)}>
            violet
          </button>
          <button className="" onClick={() => this.handleClick(7)}>
            orange
          </button>
          <button className="" onClick={() => this.handleClick(8)}>
            pink
          </button>
        </div>
      </div>
    );
  }
}

