import axios from "axios";

//Initial State
const initialState = {
  guessInputs: ["x", "x", "x", "x"],
  sideScores: [],
  secretCode: [],
};

//Action Types
const GET_NUMBERS_SUCCESS = "GET_NUMBERS_SUCCESS";

//Action Creators
const getNumber = (numbers) => ({ type: GET_NUMBERS_SUCCESS, numbers });

// Thunk Creators
export const getNumbers = () => async (dispatch) => {
  try {
    const numbers = await axios.get(
      "https://www.random.org/integers/?num=8&min=1&max=6&col=1&base=10&format=plain&rnd=new"
    );

    console.log(numbers.data);

    dispatch(getNumber(numbers.data || initialState.secretCode));
  } catch (err) {
    console.log(err);
  }
};

//Reducer
export default function numbersReducer(
  state = initialState.secretCode,
  action
) {
  switch (action.type) {
    case GET_NUMBERS_SUCCESS:
      return [...state, action.payload];
    default:
      return initialState;
  }
}
