import axios from "axios";

//Initial State
const initialState = {
  guessInputs: ["x", "x", "x", "x"],
  sideScores: [],
  secretCode: [],
};

//Action Types
const GET_NUMBERS_SUCCESS = "GET_NUMBERS_SUCCESS";
const GET_NUMBERS_FAILED = "GET_NUMBERS_FAILED";

//Action Creators
export const getNumbers = async (dispatch) => {
  try {
    const numbers = await axios.get(
      "https://www.random.org/integers/?num=8&min=1&max=6&col=1&base=10&format=plain&rnd=new"
    );

    console.log(numbers);

    dispatch({
      type: GET_NUMBERS_SUCCESS,
      payload: numbers,
    });
  } catch (err) {
    console.log(err);
    dispatch({
      type: GET_NUMBERS_FAILED,
      payload: err,
    });
  }
};

//Reducer
export default function numbersReducer(state = initialState, action) {
  switch (action.type) {
    case GET_NUMBERS_SUCCESS:
      return action.payload;
    case GET_NUMBERS_FAILED:
      return action.payload;
    default:
      return initialState;
  }
}
